mapper.select:
select mo.monitorid,moname.monitorname,mo.monitorimgurl,mo.monitormvurl,mo.kid from tbl_monitor mo ,tbl_kinder kin ,tbl_monitorname moname
where mo.kid = kin.kinderid and mo.monid = moname.monitornameid and kin.kindername = #{kindername}
limit #{page},#{limit}

select mo.monitorid,mo.monitormvname, moname.monitorname,mo.monitormvtime,mo.monitormvurl from tbl_monitor mo ,tbl_kinder kin ,tbl_monitorname moname
mo.kid = kin.kinderid and mo.monid = moname.monitornameid and kin.kindername = #{kindername}
and mo.monitormvname like CONCAT('%',#{monitormvname},'%')
and DATE_FORMAT(mo.monitormvtime, '%Y-%m-%d')>= DATE_FORMAT(#{time1}, '%Y-%m-%d')
and DATE_FORMAT(mo.monitormvtime, '%Y-%m-%d')<= DATE_FORMAT(#{time2}, '%Y-%m-%d')
limit #{page},#{limit}

select count(*) from tbl_monitor mo ,tbl_kinder kin ,tbl_monitorname moname

    mo.kid = kin.kinderid and mo.monid = moname.monitornameid
    and kin.kindername = #{kindername}

        and mo.monitormvname = #{monitormvname}


           and DATE_FORMAT(mo.monitormvtime, '%Y-%m-%d')>=  DATE_FORMAT(#{time1}, '%Y-%m-%d')   


           and DATE_FORMAT(mo.monitormvtime, '%Y-%m-%d')<=  DATE_FORMAT(#{time2}, '%Y-%m-%d')
